* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
  background: #0f1419;
  color: #e4e6eb;
  overflow-x: hidden;
}

.portfolio {
  min-height: 100vh;
}

/* Header Styles */
.header {
  position: fixed;
  top: 0;
  width: 100%;
  background: rgba(15, 20, 25, 0.95);
  backdrop-filter: blur(10px);
  z-index: 1000;
  padding: 1rem 0;
  border-bottom: 1px solid rgba(100, 115, 255, 0.1);
}

.nav {
  display: flex;
  justify-content: space-between;
  align-items: center;
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 2rem;
}

.logo {
  display: flex;
  align-items: center;
  gap: 1rem;
  font-size: 1.5rem;
  font-weight: bold;
  color: #6c7aff;
}

.logo-icon {
  width: 40px;
  height: 40px;
}

.logo-circle {
  fill: none;
  stroke: #00f5ff;
  stroke-width: 2;
  animation: rotate 4s linear infinite;
}

.logo-text {
  fill: #ffffff;
  font-size: 18px;
  text-anchor: middle;
  font-weight: bold;
}

@keyframes rotate {
  from { transform: rotate(0deg); }
  to { transform: rotate(360deg); }
}

.nav-links {
  display: flex;
  list-style: none;
  gap: 2rem;
}

.nav-links a {
  color: #ffffff;
  text-decoration: none;
  transition: color 0.3s ease;
  position: relative;
}

.nav-links a:hover {
  color: #6c7aff;
}

.nav-links a::after {
  content: '';
  position: absolute;
  bottom: -5px;
  left: 0;
  width: 0;
  height: 2px;
  background: #6c7aff;
  transition: width 0.3s ease;
}

.nav-links a:hover::after {
  width: 100%;
}

.mobile-menu-toggle {
  display: none;
  flex-direction: column;
  cursor: pointer;
  gap: 4px;
}

.hamburger-line {
  width: 25px;
  height: 3px;
  background: #6c7aff;
  transition: all 0.3s ease;
}

.mobile-menu-toggle.active .hamburger-line:nth-child(1) {
  transform: rotate(45deg) translate(5px, 5px);
}

.mobile-menu-toggle.active .hamburger-line:nth-child(2) {
  opacity: 0;
}

.mobile-menu-toggle.active .hamburger-line:nth-child(3) {
  transform: rotate(-45deg) translate(7px, -6px);
}

/* Hero Section */
.hero {
  min-height: 100vh;
  display: flex;
  align-items: flex-start;
  justify-content: center;
  background: radial-gradient(circle at 50% 50%, rgba(108, 122, 255, 0.08) 0%, rgba(139, 92, 246, 0.05) 50%, transparent 100%);
  position: relative;
  overflow: hidden;
  padding-top: 80px;
}

.hero-bg-image {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  opacity: 0.3;
  z-index: 0;
}

.hero-bg-svg {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.hero::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><circle cx="50" cy="50" r="1" fill="%2300f5ff" opacity="0.5"><animate attributeName="r" values="1;2;1" dur="2s" repeatCount="indefinite"/></circle></svg>') repeat;
  animation: sparkle 3s linear infinite;
  pointer-events: none;
}

@keyframes sparkle {
  0% { transform: translateY(0); }
  100% { transform: translateY(-100px); }
}

.hero-content {
  display: flex;
  flex-direction: column;
  gap: 2rem;
  max-width: 1200px;
  padding: 6rem 2rem 2rem;
  align-items: center;
  text-align: center;
  position: relative;
  z-index: 1;
}

.hero-title {
  font-size: 3.5rem;
  margin-bottom: 1rem;
  line-height: 1.2;
}

.glowing-text {
  background: linear-gradient(45deg, #6c7aff, #8b5cf6, #6c7aff);
  background-size: 200% 200%;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  animation: glowing 3s ease-in-out infinite;
  filter: drop-shadow(0 0 20px rgba(108, 122, 255, 0.3));
}

@keyframes glowing {
  0%, 100% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
}

.hero-subtitle {
  font-size: 1.2rem;
  color: #cccccc;
  margin-bottom: 2rem;
}

.hero-details {
  display: flex;
  flex-direction: column;
  gap: 1rem;
}

.detail-item {
  display: flex;
  align-items: center;
  gap: 1rem;
}

.detail-icon {
  width: 24px;
  height: 24px;
  fill: #6c7aff;
}

.hero-avatar {
  display: flex;
  justify-content: center;
}

.hero-image-container {
  position: relative;
  width: 300px;
  height: 300px;
  border-radius: 50%;
  overflow: hidden;
  border: 4px solid transparent;
  background: linear-gradient(45deg, #6c7aff, #8b5cf6);
  padding: 4px;
  animation: float 3s ease-in-out infinite;
}

.hero-profile-image {
  width: 100%;
  height: 100%;
  object-fit: cover;
  border-radius: 50%;
  border: 3px solid rgba(108, 122, 255, 0.5);
  filter: brightness(1.1) contrast(1.1);
  transition: all 0.3s ease;
  box-shadow: 0 0 30px rgba(108, 122, 255, 0.3), 
              inset 0 0 20px rgba(255, 255, 255, 0.1);
}

.hero-profile-image:hover {
  border-color: rgba(108, 122, 255, 0.8);
  box-shadow: 0 0 40px rgba(108, 122, 255, 0.5), 
              inset 0 0 30px rgba(255, 255, 255, 0.2);
  transform: scale(1.05);
}

.avatar-svg {
  width: 300px;
  height: 300px;
  animation: float 3s ease-in-out infinite;
}

.avatar-circle {
  animation: pulse 2s ease-in-out infinite;
}

@keyframes float {
  0%, 100% { transform: translateY(0); }
  50% { transform: translateY(-20px); }
}

@keyframes pulse {
  0%, 100% { transform: scale(1); }
  50% { transform: scale(1.05); }
}

/* Section Styles */
.section {
  padding: 6rem 0;
  opacity: 0;
  transform: translateY(50px);
  transition: all 0.8s ease;
}

.section.animate-in {
  opacity: 1;
  transform: translateY(0);
}

.container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 2rem;
}

.section-title {
  font-size: 2.5rem;
  text-align: center;
  margin-bottom: 3rem;
  position: relative;
}

.section-title::after {
  content: '';
  position: absolute;
  bottom: -10px;
  left: 50%;
  transform: translateX(-50%);
  width: 100px;
  height: 3px;
  background: linear-gradient(90deg, #6c7aff, #8b5cf6);
  border-radius: 2px;
}

/* About Section */
#about {
  position: relative;
  overflow: hidden;
}

.about-bg-image {
  position: absolute;
  top: 0;
  right: 0;
  width: 50%;
  height: 100%;
  opacity: 0.4;
  z-index: 0;
}

.about-bg-svg {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.about-content {
  max-width: 800px;
  margin: 0 auto;
  text-align: center;
  font-size: 1.1rem;
  line-height: 1.8;
  position: relative;
  z-index: 1;
}

.about-content p {
  margin-bottom: 1.5rem;
  color: #9ca3af;
}

/* Skills Section */
.skills-grid {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 2rem;
  margin-top: 2rem;
  max-width: 1000px;
  margin-left: auto;
  margin-right: auto;
}

.skill-card {
  background: #1a1f2e;
  border: 1px solid #2d3748;
  border-radius: 12px;
  padding: 2rem;
  text-align: center;
  transition: all 0.3s ease;
  animation: slideUp 0.6s ease forwards;
  opacity: 0;
  transform: translateY(30px);
  position: relative;
  overflow: hidden;
}

.skill-card::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(135deg, rgba(108, 122, 255, 0.05), rgba(139, 92, 246, 0.05));
  opacity: 0;
  transition: opacity 0.3s ease;
}

.skill-card:hover::before {
  opacity: 1;
}

.skill-card:hover {
  transform: translateY(-10px);
  border-color: #4a5568;
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.3);
}

@keyframes slideUp {
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.skill-icon-wrapper {
  position: relative;
  z-index: 2;
  margin-bottom: 1rem;
  display: flex;
  justify-content: center;
  align-items: center;
  height: 60px;
}

.skill-icon {
  width: 50px;
  height: 50px;
  transition: transform 0.3s ease;
}

.skill-card:hover .skill-icon {
  transform: scale(1.1);
}

.skill-name {
  font-size: 1.2rem;
  font-weight: 700;
  color: #ffffff;
  margin-bottom: 0.5rem;
  position: relative;
  z-index: 2;
}

.skill-description {
  font-size: 0.85rem;
  color: #9ca3af;
  position: relative;
  z-index: 2;
  line-height: 1.4;
}

/* Passion Section */
.passion-content {
  display: grid;
  grid-template-columns: 1fr 2fr;
  gap: 4rem;
  align-items: center;
}

.passion-icon {
  display: flex;
  justify-content: center;
}

.gaming-icon {
  width: 200px;
  height: 200px;
  animation: gaming-pulse 2s ease-in-out infinite;
}

.controller-body {
  fill: rgba(108, 122, 255, 0.1);
  stroke: #6c7aff;
  stroke-width: 2;
}

.button, .action-btn {
  fill: #8b5cf6;
  animation: button-glow 1.5s ease-in-out infinite alternate;
}

.dpad {
  fill: #6c7aff;
}

@keyframes gaming-pulse {
  0%, 100% { transform: scale(1); }
  50% { transform: scale(1.1); }
}

@keyframes button-glow {
  0% { fill: #8b5cf6; }
  100% { fill: #a78bfa; }
}

.passion-text h3 {
  font-size: 2rem;
  margin-bottom: 1rem;
}

.passion-text p {
  font-size: 1.1rem;
  line-height: 1.8;
  color: #9ca3af;
  margin-bottom: 2rem;
}

.passion-details {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 2rem;
  margin-bottom: 2rem;
}

.passion-item h4 {
  color: #6c7aff;
  margin-bottom: 0.5rem;
  font-size: 1.1rem;
}

.passion-item p {
  font-size: 0.95rem;
  color: #9ca3af;
  margin: 0;
}

.gaming-stats {
  display: flex;
  gap: 3rem;
}

.stat {
  text-align: center;
}

.stat-number {
  display: block;
  font-size: 2rem;
  font-weight: bold;
  color: #00f5ff;
}

.stat-label {
  font-size: 0.9rem;
  color: #999999;
}

/* School Section */
.school-content {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 3rem;
  align-items: start;
}

.school-info {
  text-align: center;
}

.school-info h3 {
  font-size: 1.8rem;
  color: #00f5ff;
  margin-bottom: 1rem;
}

.school-info p {
